mapper.insert:
insert into board(
	board_no, member_no, board_content, board_lat, board_lon,
	board_reply_valid, board_like_valid,
	board_hide, board_kid, board_size
) 
values(
	board_seq.nextval, #{memberNo}, #{boardContent}, #{boardLat}, #{boardLon},
	#{boardReplyValid}, #{boardLikeValid},
	#{boardHide}, #{boardKid}, #{boardSize}
)
