mapper.select:
select * from ( select b.board_no"board_no", bl.member_no, m.member_nick, mp.attachment_no, bl.like_check "check", bl.like_time "alarm_time", 0 "type", to_char(bl."like_member_count") "etc" from board b inner join ( select bl.*"like_member_count" from board_like bl inner join ( select bl.board_no, to_char(bl.like_time, 'YYYY-MM-DD'), max(bl.like_time)"like_time", count(bl.member_no)"like_member_count" from board b inner join board_like bl on b.board_no = bl.board_no where b.member_no = #{memberNo} group by bl.board_no, to_char(bl.like_time, 'YYYY-MM-DD')) ) bl on b.board_no = bl.board_no left outer join member_profile mp on bl.member_no = mp.member_no inner join member m on bl.member_no = m.member_no where b.member_no = #{memberNo}
union
select null"board_no", m.member_no, m.member_nick,mp.attachment_no, f.follow_check"check", f.follow_date"alarm_time", 2"type", f.follow_confirm"etc" from follow f inner join member m on f.member_no = m.member_no left outer join member_profile mp on m.member_no = mp.member_no where f.follow_follower = #{memberNo}
union
select br2.board_no, m.member_no, m.member_nick, mp.attachment_no, r.reply_check"check", r.reply_date"alarm_time", 3"type", to_char(br2."etc") from board_reply br inner join reply r on br.reply_no = r.reply_no inner join ( select b.board_no, to_char(r.reply_date, 'YYYY-MM-DD'), max(r.reply_date)"alarm_time",count(r.reply_no)"etc" from board_reply br inner join board b on br.board_no = b.board_no inner join reply r on br.reply_no = r.reply_no where b.member_no = #{memberNo} group by b.board_no, to_char(r.reply_date, 'YYYY-MM-DD') )br2 on br.board_no = br2.board_no and br2."alarm_time" = r.reply_date inner join member m on m.member_no = r.reply_member_no left outer join member_profile mp on mp.member_no = m.member_no

mapper.update:
	update board_like set like_check = 1 where board_no in (
	    select b.board_no from board  b
	        inner join board_like bl on b.board_no = bl.board_no
	    where b.member_no = #{memberNo}
    ) and like_check = 0

update reply set reply_check = 1 where reply_no in (
	select r.reply_no from board b 
	    inner join board_reply br on b.board_no = br.board_no
	    inner join reply r on r.reply_no = br.reply_no
	where b.member_no = #{memberNo}
) and reply_check = 0
